mapper.update:
update orders
pay_status = #{payStatus}, pay_method = #{payMethod}, cancel_time = #{cancelTime}, cancel_reason = #{cancelReason}, status = #{status}, delivery_status = #{deliveryStatus}, delivery_time = #{deliveryTime}, checkout_time = #{checkoutTime}, rejection_reason = #{rejectionReason}, estimated_delivery_time = #{estimatedDeliveryTime},
where id = #{id}

mapper.select:
select * from orders where user_id=#{userId}
and status=#{status}
order by order_time desc

select * from orders


        number like concat('%',#{number},'%')


        and phone=#{phone}


        and order_time between #{beginTime} and #{endTime}


        and status=#{status}


order by order_time desc

select count(*)
from orders


    and status=#{status}

select count(*) from orders


        order_time between #{begin} and #{end}


    and status=#{status}
